    fetch('/logout', {
        method:'DELETE'
    })
    .then(res => {
        if(res.ok){
                updateUser(null)
                history.push('/authentication')
            }
    })

    return (
            <Nav>
                <NavH1>Login</NavH1>
                <ul>
                    <li><Link to='/'>Home</Link></li>
                    <li onCLick={handleLogout}>Logout</li>
                </ul>
            </Nav>
    )
}

export default Nav

const NavH1 = styled.h1`
font-family: 'Splash', cursive;
`
