  const emojis = makeEmojiList(minutes_to_read);

  return (
    <article key={id}>
      <h3>
        <Link to={`/articles/${id}`}>{title}</Link>
      </h3>
      <small>
        {date} â€¢ {emojis} {minutes_to_read} min read
      </small>
      <p>{preview}</p>
    </article>
  );
}

export default ArticleItem;
